$destDir = Join-Path $PSScriptRoot "..\Engine\DebugLogs"; New-Item -ItemType Directory -Force -Path $destDir | Out-Null; $f = Get-ChildItem $env:TEMP -Filter "astrild_failed_frag_*.frag" | Sort-Object LastWriteTime -Descending | Select-Object -First 1; if ($f) { Copy-Item $f.FullName -Destination (Join-Path $destDir $f.Name) -Force; Write-Output "Copied $($f.FullName) to $destDir" } else { Write-Output "No fragment dump found in $env:TEMP" }